    /*Hàm thêm tất cả các dòng trong Table vào bảng chi tiết hóa đơn*/
    private void ThemCTHDTuTable(int MaHD) {
        for (int i = 0; i < tblCTHD.getRowCount(); i++) {

            int IDSanPham = Integer.parseInt(tblCTHD.getValueAt(i, 7).toString());
            int SoLuong = Integer.parseInt(tblCTHD.getValueAt(i, 3).toString());
            double ThanhTien = ChuyenDoi.ChuyenTien(tblCTHD.getValueAt(i, 5).toString());
            String KichThuoc = tblCTHD.getValueAt(i, 6).toString();

            ThemChiTietHoaDon(MaHD, IDSanPham, SoLuong, ThanhTien, KichThuoc);
        }
    }

    private void ThemChiTietHoaDon(int MaHD, int MaSP, int SoLuong,
            double ThanhTien, String KichThuoc) {

        ChiTietHoaDon cthd = new ChiTietHoaDon();
        cthd.setMaHD(MaHD);
        cthd.setMaSP(MaSP);
        cthd.setSoLuong(SoLuong);
        cthd.setThanhTien(ThanhTien);
        cthd.setKichThuoc(KichThuoc);

        BLLChiTietHoaDon.Them(cthd);
    }

